import React, { useState, useEffect } from 'react';
import { assets } from '../../constants';
import './Main.css';
const Main = ({ selectedPunk, punkListData }) => {
  const [activePunk, setActivePunk] = useState(punkListData[0]);
  useEffect(() => {
    setActivePunk(punkListData[selectedPunk]);
  }, [punkListData, selectedPunk]);
  return (
    <div className='main'>
      <div className='mainContent'>
        <div className='punkHighlight'>
          <div className='punkContainer'>
            <img
              className='selectedPunk'
              src='https://lh3.googleusercontent.com/WrmpbMJMKmGfjJRCu2b0Bp_Pk2iPZcuRXyqPj7p3-I19DKHlIluigjdiwsnUZA28ssIQcydOnYgD2f6Qra05gFMZHWuojV1JSbM=s250'
              alt='img'
            />
          </div>
        </div>
        <div className='punkDetails' style={{ color: '#fff' }}>
          <div className='title'>Bedgad</div>
          <span className='itemNumber'>#3</span>
        </div>
        <div className='owner'>
          <div className='ownerImageContainer'>
            <img src={activePunk.image_preview_url} alt='' />
          </div>
          <div className='ownerDetails'>
            <div className='ownerNameAndHandle'>
              <div>0xCd9c3e23CfEa4e8012BE0353B5729B5b0767f53a</div>
              <div className='ownerHandle'>@jinwangartist </div>
            </div>
            <div className='ownerLink'>
              <img src={assets.instagramLogo} alt='' />
            </div>

            <div className='ownerLink'>
              <img src={assets.twitterLogo} alt='' />
            </div>
            <div className='ownerLink'>
              <img src={assets.moreLogo} alt='' />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Main;
